import {  useState  } from 'react'
import styles from './legendabox.module.css'

export default function LegendaBox({userImage, nome, tempo}) {
    const [textoLegenda, setTextoLegenda] = useState(true)


    function toggleTranslate() {
        setTraduzir(!traduzir)
    }

    return (
        <div className={styles.container}>
            <div className={styles.head}>
                <img src={userImage} alt='imagem do usuário'/>
                <span>{nome}</span>
                <span>{tempo}</span>
            </div>
            <p className={styles.legenda}>
                {traduzir ? 'Oi, meu nome é Lucas e eu tenho trabalhado duro para me tornar um desenvolvedor Web.' : 'Hi, my name is Lucas, and I have been working hard to become a Web developer.'}
            </p>
            <button onClick={toggleTranslate}>{traduzir ?'Ver tradução': 'Ver original'}</button>
        </div>
    )
}
