import {  useState  } from 'react'
import styles from './followbox.module.css'

const [seguindo, setSeguindo] = useState('Seguir')

function handleChange() {
    setSeguindo(!seguindo)
}

export default function FollowBox() {
    return (
        <div>
            <p>logoeu.web</p>
            <button onClick={handleChange}>{seguindo? 'Seguir' : 'Seguindo'}</button>
        </div>
    )
}
